rand=randperm(m);
Trainfeature=features(rand(1:10),:);
Trainlabel=labels(rand(1:10),:);
Inputfeature=features(rand(11:1010),:);
Inputlabel=labels(rand(11:1010),:);
testFeature=features(rand(1011:1050),:);
testDistribution=labels(rand(1011:1050),:);
% Trainfeature=features(1:10,:);
% Trainlabel=labels(1:10,:);
% Inputfeature=features(11:1010,:);
% Inputlabel=labels(11:1010,:);
% testFeature=features(1011:1050,:);
% testDistribution=labels(1011:1050,:);

ldl_metrics=[];
for c=1:3

    X1 = Trainfeature';
    X2 = Inputfeature';
    y =Trainlabel';
    option.t = 1\1000;
    option.b = 10;
    option.iter = 100;
    [W,a,obj] = ALDL_kMMD(X1,X2,y,option);
    [d,f]=sort(a, 'descend');%f是位置向量

    %取f的前十个位置
    g=f(1:10);%%%%%%%%%%%%%%%%%smprank
    a1=Inputlabel(g,:);
    b1=Inputfeature(g,:);

    Trainlabel=[Trainlabel;a1];
    Trainfeature=[Trainfeature;b1];

    % Prediction
    item=eye(size(Trainfeature,2),size(Trainlabel,2));
    save('yeastYeast_alpha.mat','Trainfeature', 'Trainlabel');
    [weights,fval] = bfgslldTrain(@bfgsProcess,item);
    preDistribution2 = lldPredict(weights,testFeature);
    [disName, distance] = computeMeasures(testDistribution, preDistribution2);
    ldl_metrics=[ldl_metrics;distance];
    %只做一个数据的预测
end

save('ldl_metrics_results.mat','ldl_metrics');

